function replyMsg(replyToken, messages) {
  var url = 'https://api.line.me/v2/bot/message/reply';
  var options = {
    'headers': {
      'Content-Type': 'application/json; charset=UTF-8',
      'Authorization': 'Bearer ' + CHANNEL_TOKEN
    },
    'method': 'post',
    'payload': JSON.stringify({
      'replyToken': replyToken,
      'messages': messages
    })
  };
  UrlFetchApp.fetch(url, options);
}

function getOrCreateFolder(parentFolderId, groupId) {
  var parent = DriveApp.getFolderById(parentFolderId);
  var folders = parent.getFoldersByName(groupId);
  return folders.hasNext() ? folders.next() : parent.createFolder(groupId);
}

function toDrive(messageId, mimeType, ext, parentFolderId) {
  var url = "https://api-data.line.me/v2/bot/message/" + messageId + "/content";
  var headers = {
    "headers": { "Authorization": "Bearer " + CHANNEL_TOKEN }
  };

  try {
    Logger.log("üì• ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÑ‡∏ü‡∏•‡πå‡∏à‡∏≤‡∏Å LINE messageId: " + messageId);
    Logger.log("üìÇ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡πÑ‡∏õ‡∏¢‡∏±‡∏á‡πÇ‡∏ü‡∏•‡πÄ‡∏î‡∏≠‡∏£‡πå: " + parentFolderId);
    Logger.log("üìÑ MIME: " + mimeType + " | EXT: " + ext);

    var content = UrlFetchApp.fetch(url, headers);
    var blob = content.getBlob();

    // ‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Å‡∏£‡∏ì‡∏µ blob ‡∏ß‡πà‡∏≤‡∏á‡∏´‡∏£‡∏∑‡∏≠ null
    if (!blob || blob.getBytes().length === 0) {
      throw new Error("Blob is empty. Cannot upload.");
    }

    var fileBlob = Utilities.newBlob(blob.getBytes(), mimeType, messageId + ext);
    var folder = DriveApp.getFolderById(parentFolderId);
    var file = folder.createFile(fileBlob);

    Logger.log("‚úÖ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à: " + file.getName());

    return 'https://drive.google.com/file/d/' + file.getId() + '/view';
  } catch (e) {
    Logger.log("‚ùå Error uploading to Google Drive: " + e.message);
    return null;
  }
}


function handleFileMessage(event) {
  var fileName = event.message.fileName;
  var fileType = fileName.split('.').pop();
  var mimetype = getMimeType(fileType);
  var groupId = (event.source && event.source.groupId) ? event.source.groupId : "individual";

  if (mimetype !== "undefined") {
    var messageId = event.message.id;
    var url = "https://api-data.line.me/v2/bot/message/" + messageId + "/content";
    var headers = {
      "headers": { "Authorization": "Bearer " + CHANNEL_TOKEN }
    };

    try {
      var content = UrlFetchApp.fetch(url, headers);
      var blob = content.getBlob();
      var fileBlob = Utilities.newBlob(blob.getBytes(), mimetype, fileName);
      var folder = getOrCreateFolder(GDRIVE_FOLDER_ID, groupId);
      var fileId = folder.createFile(fileBlob).getId();
