        'type': 'text',
        'text': 'üìÅ ‡∏ä‡∏∑‡πà‡∏≠‡πÑ‡∏ü‡∏•‡πå: ' + fileName +
                '\n‡∏à‡∏≤‡∏Å‡∏Å‡∏•‡∏∏‡πà‡∏°: ' + groupId +
                '\n‡πÄ‡∏Å‡πá‡∏ö‡πÑ‡∏ß‡πâ‡∏ó‡∏µ‡πà Google Drive:\nhttps://drive.google.com/file/d/' + fileId + '/view'
      }];
    } catch (e) {
      Logger.log("Error uploading to Google Drive: " + e);
      return [{ 'type': 'text', 'text': '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡∏Ç‡∏ì‡∏∞‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î' }];
    }
  } else {
    return [{ 'type': 'text', 'text': '‡πÑ‡∏°‡πà‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏ô‡∏µ‡πâ' }];
  }
}

function getMimeType(fileType) {
  var mimeTypes = {
    "pdf": "application/pdf",
    "zip": "application/zip",
    "rar": "application/vnd.rar",
    "7z": "application/x-7z-compressed",
    "doc": "application/msword",
    "xls": "application/vnd.ms-excel",
    "ppt": "application/vnd.ms-powerpoint",
    "docx": "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
    "xlsx": "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
    "pptx": "application/vnd.openxmlformats-officedocument.presentationml.presentation",
    "txt": "text/plain",
    "csv": "text/csv",
    "json": "application/json",
    "xml": "application/xml",
    "html": "text/html",
    "mp4": "video/mp4",
    "mp3": "audio/mpeg",
    "png": "image/png",
    "gif": "image/gif",
    "jpg": "image/jpeg",
    "jpeg": "image/jpeg"
  };
  return mimeTypes[fileType] || "application/octet-stream"; // fallback for unknown
}

function doPost(e) {
  var value = JSON.parse(e.postData.contents);
  var event = value.events[0];
  var replyToken = event.replyToken;
  var groupId = (event.source && event.source.groupId) ? event.source.groupId : "individual";
 var messageType = event.message.type;

if (messageType === 'file') {
  var fileType = event.message.fileName.split('.').pop().toLowerCase();
  var mime = getMimeType(fileType);
  var replyMessage = [];
  var fileId = event.message.id;

  var imageTypes = ["jpg", "jpeg", "png", "gif", "bmp", "tiff", "webp"];
  var videoTypes = ["mp4", "mov", "avi", "wmv", "mkv"];
  var audioTypes = ["mp3", "m4a", "wav", "ogg"];
