        toDrive(fileId, mime, "." + fileType, GDRIVE_FOLDER_VIDEO_ID)
    }];
  } else if (audioTypes.includes(fileType)) {
    replyMessage = [{
      'type': 'text',
      'text': 'üîä ‡πÑ‡∏ü‡∏•‡πå‡πÄ‡∏™‡∏µ‡∏¢‡∏á\n‡πÄ‡∏Å‡πá‡∏ö‡∏ó‡∏µ‡πà:\n' +
        toDrive(fileId, mime, "." + fileType, GDRIVE_FOLDER_AUDIO_ID)
    }];
  } else if (documentTypes.includes(fileType)) {
    replyMessage = [{
      'type': 'text',
      'text': 'üìÑ ‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£‡∏ó‡∏±‡πà‡∏ß‡πÑ‡∏õ\n‡πÄ‡∏Å‡πá‡∏ö‡∏ó‡∏µ‡πà:\n' +
        toDrive(fileId, mime, "." + fileType, GDRIVE_FOLDER_DOCUMENT_ID)
    }];
  } else if (compressedTypes.includes(fileType)) {
    replyMessage = [{
      'type': 'text',
      'text': 'üóúÔ∏è ‡πÑ‡∏ü‡∏•‡πå‡∏ö‡∏µ‡∏ö‡∏≠‡∏±‡∏î (zip/rar)\n‡πÄ‡∏Å‡πá‡∏ö‡∏ó‡∏µ‡πà:\n' +
        toDrive(fileId, mime, "." + fileType, GDRIVE_FOLDER_DOCUMENT_ID)
    }];
  } else {
    replyMessage = [{
      'type': 'text',
      'text': '‚ùå ‡πÑ‡∏°‡πà‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡πÑ‡∏ü‡∏•‡πå‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏ô‡∏µ‡πâ: .' + fileType
    }];
  }

  replyMsg(replyToken, replyMessage);
}
